using System.Collections;using System.Collections.Generic;using UnityEngine;public class HardBlockScript : MonoBehaviour {    public int hp = 2;    public GameObject obj;    public SceneScript script;    void Start()    {        obj = GameObject.Find("SceneScript");        script = obj.GetComponent<SceneScript>();    }	    void OnCollisionEnter()    {        hp--;        if (hp == 0)        {            Destroy(gameObject);            script.score += 5;            script.blockCt--;            Debug.Log("Hit");        }    }}