using System.Collections;using System.Collections.Generic;using UnityEngine;public class PenetrateBlockScript : MonoBehaviour {    public Collider coll;    public GameObject obj;    public SceneScript script;    void Start()    {        obj = GameObject.Find("SceneScript");        script = obj.GetComponent<SceneScript>();        coll = GetComponent<Collider>();        coll.isTrigger = true;    }    void OnTriggerEnter()    {        Destroy(gameObject);        script.score += 5;        script.blockCt--;        Debug.Log("Hit");               }}