using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.SceneManagement;public class SceneScript : MonoBehaviour {    public int life = 3;    public int score = 0;    public int blockCt = 18;    public bool Clear = false;    void Start()    {        DontDestroyOnLoad(this);    }    void Update()    {        if(blockCt <= 0 && Clear == false)        {            SceneManager.LoadScene("GameClear");            Clear = true;        }    }}